package kd

import (
	"strings"
	"testing"
)

func TestRemoveDigits(t *testing.T) {
	st := []struct {
		name string
		num  string
		k    int
		exp  string
	}{
		{"k eq to len(s)", "123", 3, "0"},
		{"testcase1", "1432219", 3, "1219"},
		{"testcase2", "10200", 1, "200"},
		{"testcase3", "123456789", 2, "1234567"},
		{"testcase4", "2228678439", 4, "222439"},
		{"failed1", "100", 1, "0"},
		{"failed2", "1173", 2, "11"},
		{"failed3", "5337", 2, "33"},
		{"failed4", "1234567890", 9, "0"},
		{"failed5", "",
			111, ""},
	}
	for _, tt := range st {
		t.Run(tt.name, func(t *testing.T) {
			out := removeKDigits(tt.num, tt.k)
			if out != tt.exp {
				t.Fatalf("with input num:%s and k:%d wanted %s but got %s", tt.num, tt.k, tt.exp, out)
			}
			t.Log("pass")
		})
	}
}

func TestRemoveDigitsUseStack(t *testing.T) {
	st := []struct {
		name string
		num  string
		k    int
		exp  string
	}{
		{"k eq to len(s)", "123", 3, "0"},
		{"testcase1", "1432219", 3, "1219"},
		{"testcase2", "10200", 1, "200"},
		{"testcase3", "123456789", 2, "1234567"},
		{"testcase4", "2228678439", 4, "222439"},
		{"failed1", "100", 1, "0"},
		{"failed2", "1173", 2, "11"},
		{"failed3", "5337", 2, "33"},
		{"failed4", "1234567890", 9, "0"},
		{"failed5", "",
			111, ""},
	}
	for _, tt := range st {
		t.Run(tt.name, func(t *testing.T) {
			out := useStack(tt.num, tt.k)
			if !strings.EqualFold(out, tt.exp) {
				t.Fatalf("with input num:%s and k:%d wanted +%s+ but got +%s+", tt.num, tt.k, tt.exp, out)
			}
			t.Log("pass")
		})
	}
}
